(function () {
  tinymce.PluginManager.add('mark-button', function (editor, url) {
    editor.on('init', function () {
      editor.formatter.register('mark-button', {
        inline: 'mark'
      });
    });

    editor.addButton('mark-button', {
      image: 'data:image/svg+xml;base64,',
      title: 'Выделить текст',
      onclick: function () {
        tinymce.activeEditor.formatter.toggle('mark-button');
      }
    });
  });
})();